import React, { useState } from 'react';
import './searchUser.css';
import { Link } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';

function SearchUser() {
  const [Key, setKey] = useState('');
//   const [data, setData] = useState([]);
  const [users, setUsers] = useState([]);


  const handleSearch = async (userKey) => {
    try {
      const response = await fetch(`http://localhost:8000/api/findkey/${userKey}`);
      const result = await response.json();
      SETuSER(result);
    } catch (error) {
      console.error("Error fetching data:", error);
    }

        

   };

  const deleteUser = async(userId) =>{
    await axios.delete(`http://localhost:8000/api/delete/${userId}`)
    .then((respones)=>{
      setUsers((prevUser)=> prevUser.filter((user)=> user.fname !== userId))
      toast.success(respones.data.msg, {position: 'top-right'})
    })
    .catch((error) =>{
      console.log(error);
    })
}


  return (
    <div className="SearchUser">
      <h1>Search User Data</h1>
      <Link to={"/"} className='addButton'>Back</Link>
      <input
        type="text"
        value={Key}
        onChange={(e) => setKey(e.target.value)}
        placeholder="Enter search key"
      />
      <button onClick={handleSearch}>Search</button>

      <div className="results">
        {users.length > 0 ? (
          <ul>
            {users.map((item, index) => (
              <li key={index}>{JSON.stringify(item)}</li>
            ))}
          </ul>
        ) : (
          <p>No results found</p>
        )}
      </div>
      <div className='userTable'>
        <Link to={"/add"} className='addButton'>Add User</Link>
        <Link to={"/search"} className='addButton'>Search User</Link>
        <table border={1} cellPadding={10} cellSpacing={0}>
            <thead>
                <tr>
                    <th>S.No.</th>
                    <th>name</th>
                    <th>Email</th>
                    <th>Department</th>
                    <th>Address</th>
                    <th>Salary</th>
                    <th>Mobile</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {
                    users.map((user, index)=>{
                        return(
                        <tr key={user.fname}>
                            <td>{index + 1}</td>
                            <td>{user.fname} {user.lname}</td>
                            <td>{user.email}</td>
                            <td>{user.sector}</td>
                            <td>{user.address}</td>
                            <td>{user.salary}</td>
                            <td>{user.mobile}</td>
                            <td className='actionButtons'>
                                <button onClick={()=> deleteUser(user.fname)}><i className="fa-solid fa-trash-can"></i></button>
                                <Link to={`/edit/`+user.fname}><i className="fa-solid fa-pen-to-square"></i></Link>
                            </td>
                        </tr>
                        )
                    })
                }
                
            </tbody>
        </table>
    </div>
    </div>
  );
}

export default SearchUser;
